    /* Codility 100%*/
    public int solution(int [] A) {

        int maxSlice = Integer.MIN_VALUE;
        int largestMinus = Integer.MIN_VALUE;

        //First search for largest minus if exist
        for(int i=0; i<A.length;i++){
            if(A[i] <= 0){
                largestMinus = Math.max(largestMinus, A[i]);
            }
        }

        //Then process slices sums as positive integers
        for(int i=1; i<A.length; i++){
            A[i] = Math.max(A[i], A[i-1]+A[i]) ;
            A[i] = Math.max(0, A[i]);
        }

        for(int i=0; i<A.length; i++){
            maxSlice = Math.max(maxSlice, A[i]);
        }

        if(largestMinus != Integer.MIN_VALUE && maxSlice == 0) {
            maxSlice = largestMinus;
        }

        return maxSlice;
    }

    public int brutForce(int[] A) {

        int maxSlice = Integer.MIN_VALUE;
        int maxIndex = Integer.MIN_VALUE;

        for(int p=0; p<A.length; p++){
            int tmpSlice = 0;
            for(int k=p; k<A.length; k++) {
                tmpSlice+=A[k];
                maxSlice = Math.max(maxSlice, tmpSlice);
            }
        }

        return maxSlice;
    }
